import { useState } from "react";
import Select from "react-select";
import "./App.css";
import HeaderCalculator from "./HeaderCalculator";

const Calculator = () => {
  const selectOption = [
    {
      value: "fermenterte meieriprodukter",
      label: "Fermenterte meieriprodukter",
    },
    {
      value: "matfett og matfettblandinger",
      label: "Matfett og matfettblandinger",
    },
    { value: "middagsretter", label: "Middagsretter" },
    {
      value: "grønnsaker, frukt, bær og nøtter",
      label: "Grønnsaker, frukt, bær og nøtter",
    },
  ];

  const selectBread = [
    { value: "yoghurtUtenTilsatt", label: "Yoghurt uten tilsatt smak" },
    { value: "yoghurtMedTilsatt", label: "Yoghurt med tilsatt smak" },
    { value: "kefir", label: "Kefir" },
    { value: "romme", label: "Rømme" },
    { value: "syrnet melk", label: "Syrnet melk" },
  ];

  const selectFat = [
    { value: "margarin og smor", label: "Margarin og smør" },
    { value: "matolje", label: "Matolje" },
    { value: "frityrfett", label: "Frityrfett" },
    { value: "majones", label: "Majones" },
    { value: "dressing", label: "Dressing" },
    { value: "tran", label: "Tran" },
  ];

  const selectVeg = [
    { value: "lys lapskaus", label: "Lapskaus" },
    { value: "fiskegrateng", label: "Fiskegrateng" },
    { value: "kjottkaker i brun saus", label: "Kjøttkaker i brun saus" },
    { value: "lassagne", label: "Lassagne" },
  ];

  const selectGrønnsaker = [
    {
      value: "grønnsaker",
      label:
        "Grønnsaker, rotfrukter, belgvekster (unntatt peanøtter) og poteter",
    },
    {
      value: "produktene kan være foredlet",
      label: "Produktene kan være foredlet",
    },
    {
      value: "uforedlede krydderurter omfattes også",
      label: "Uforedlede krydderurter omfattes også",
    },
  ];

  const [selectsGroup, setSelectGroups] = useState("");

  const [selectsProduct, setSelectProduct] = useState("");

  const handlerGroup = (event) => {
    setSelectGroups(event.value);
  };

  const handlerProduct = (event) => {
    setSelectProduct(event.value);
  };

  return (
    <div className="vstack gap-3 container">
      <HeaderCalculator />

      <div className="row">
        <div className="col-md-6">
          <h3>Legg inn næringsinnhold</h3>

          <label for="matnavn" class="form-label">
            <strong>Matvarenavn</strong>
          </label>
          <div class="input-group mb-3">
            <input
              type="text"
              class="form-control"
              aria-describedby="matnavn"
            />
          </div>

          <label for="matgruppe" class="form-label">
            <strong>Matvaregruppe</strong>
          </label>
          <Select
            placeholder={<div>Velg matvaregruppe</div>}
            className="form-select-md mb-3"
            onChange={handlerGroup}
            options={selectOption}
          />

          {selectsGroup === "fermenterte meieriprodukter" && (
            <div>
              <label for="mat" class="form-label">
                <strong>Matkategori</strong>
              </label>
              <Select
                placeholder={<div>Velg mat</div>}
                className="form-select-md mb-3"
                onChange={handlerProduct}
                options={selectBread}
              />
            </div>
          )}

          {selectsGroup === "matfett og matfettblandinger" && (
            <div>
              <label for="mat" class="form-label">
                <strong>Matkategori</strong>
              </label>
              <Select
                placeholder={<div>Velg mat</div>}
                className="form-select-md mb-3"
                onChange={handlerProduct}
                options={selectFat}
              />
            </div>
          )}

          {selectsGroup === "middagsretter" && (
            <div>
              <label for="mat" class="form-label">
                <strong>Matkategori</strong>
              </label>
              <Select
                placeholder={<div>Velg mat</div>}
                className="form-select-md mb-3"
                onChange={handlerProduct}
                options={selectVeg}
              />
            </div>
          )}

          {selectsGroup === "grønnsaker, frukt, bær og nøtter" && (
            <div>
              <label for="mat" class="form-label">
                <strong>Matkategori</strong>
              </label>
              <Select
                placeholder={<div>Velg mat</div>}
                className="form-select-md mb-3"
                onChange={handlerProduct}
                options={selectGrønnsaker}
              />
            </div>
          )}
        </div>

        <div className="col-md-6">
          <h3>Mulige ernærings- og helsepåstander</h3>
          <p>
            Trykk på "søk"-knappen for å se resultatet. Først må du sette
            ernæringsverdiene inne i ernæringskolonnen. Resultatet vises på
            høyre side. Hvis en "feil" oppstår, hold musepekeren over feilikonet
            i venstre kolonne for å se detaljene om den spesifikke feilen.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
